const http = require\('http'\);

// Check each critical component
const files = [
  '/src/components/Viewport.tsx',
  '/src/components/SlideCanvas.tsx',
  '/src/canvas/drawSlideContent.ts',
  '/src/store/useAppStore.ts',
];

let checked = 0;
files.forEach\(f => {
  http.get\('http://localhost:5173' + f, \(res\) => {
    let data = '';
    res.on\('data', c => data += c\);
    res.on\('end', \(\) => {
      const ok = res.statusCode === 200 && !data.includes\('Error'\) && !data.includes\('SyntaxError'\);
      console.log\(f + ': ' + \(ok ? 'OK' : 'ISSUE \(status=' + res.statusCode + '\)'\)\);
      checked++;
    }\);
  }\);
}\);
" 2>&1)
